<script type="text/javascript">
    document.getElementById("Globalnav.Products").onclick = function() {toGggoleMenu("productPopup")};
    document.getElementById("Globalnav.Resources").onclick = function() {toGggoleMenu("resourcePopup")};
    document.getElementById("Globalnav.Capabilities").onclick = function() {toGggoleMenu("CapabilitiesPopup")};

    let menuIds = ["productPopup","resourcePopup","CapabilitiesPopup"]
    function toGggoleMenu(id) {
        for (let i = 0; i < menuIds.length; i++) {
            if(menuIds[i] != id){
                var y = document.getElementById(menuIds[i]);
                y.style.display = "none";
            }
        }
        var x = document.getElementById(id);
        if (x.style.display === "none") {
            x.style.display = "block";
        } else {
            x.style.display = "none";
        }
    }
</script>
